    def center_crop_resize(im):
        width, height = im.size
        d = min(width, height)
        left = (width - d) / 2
        upper = (height - d) / 2
        right = (width + d) / 2
        lower = (height + d) / 2
        return im.crop((left, upper, right, lower)).resize((EDICT_prefs['max_size'], EDICT_prefs['max_size']))
