def elapsed(start_time, end_time=None):
    if end_time is None:
        end_time = time.time()
    return f"{end_time-start_time:.0f}s"

def its(step_time):
    if step_time < 1.0:
        its = 1.0 / step_time
        output = f"{its:.1f} it/s"
    else:
        sit = step_time
        output = f"{sit:.1f} s/it"
    return output

