def create_pattern(filename, glob=False):
    base, ext = os.path.splitext(filename)
    last_digits = ""
    non_digits = ""
    for c in base:
        if c.isdigit():
            last_digits += c
        else:
            non_digits += c
            last_digits = ""
    padding = min(4, len(last_digits))
    base_name = base[:-padding]
    if glob:
        pattern = f"{base_name}*{ext}"
    else:
        pattern = f"{base_name}%0{padding}d{ext}"
    return pattern

